import React, { useEffect,useState } from 'react';
import logo from './logo.svg';
import './css/App.css';
import {Header} from './components/header.js'
import {Main} from './components/main.js'
import { Preloader } from 'react-materialize'
import axios from 'axios'
import Input from 'react-materialize/lib/Input';

function App() {

  const [request, setRequest] = useState({
    loading: false,
    payload: null,
  });
  const [number , setNumber] = useState()

  // const responseMock = {
  //   student: "Felipe Domingues",
  //   element: "Tório",
  //   symbol: "Th",
  //   number: "90",
  //   word: "loucão",
  //   artdesc:"a arte criada remete ao elemento urânio a arte criada remete ao elemento urânio a arte criada remete ao elemento urânio",
  //   elementdesc:"a arte criada remete ao elemento urânio a arte criada remete ao elemento urânio a arte criada remete ao elemento urânio",
  //   avatar: "g"
  // }
  const handleChange =(number) => {
    console.log(number)
    // setValue(number)

  }

  useEffect(() => {
    // Note that this replaces the entire object and deletes user key!
    setRequest({ loading: true });
    axios.get('http://www.mocky.io/v2/5d96653a3b00001100c310d3')
    //axios.get('http://www.mocky.io/v2/5d73c0f8330000373308186f')
    //axios.get('https://localhost:4000/elements/') 
      .then(response => {
        setRequest({
          loading: false,
          payload: response.data,

        });
      });
  }, []);


const { loading, payload } = request;
setRequest({payload: payload.find(item => item.number === 13) })
//const filteredPayload = payload && payload.find(item => item.number === number)

  return (
    <div className="App">
      <Header/> 
      <input
        type= "number"
       // onChange ={(event) =>setNumber(event.target.value)}
        //value={value}
        placeholder={"digite o número atômico do elemento"}
        style={{
          width: 280
        }} />
      { loading? 
      <Preloader flashing  size = "small"/>:

      <Main 
        student = { payload.student}
        element= {  payload.element}
        symbol={ payload.symbol}
        number = { payload.number}
        word ={ payload.word}
        artdesc={ payload.artdesc}
        elementdesc={ payload.elementdesc}
        handleChange ={handleChange}
        payload = {payload}
      />

      }
    </div>
  );
}

export default App;
